(415, 55, 'field_54d8f918e82e4', 'a:11:{s:3:"key";s:19:"field_54d8f918e82e4";s:5:"label";s:17:"Producers Content";s:4:"name";s:17:"producers_content";s:4:"type";s:7:"wysiwyg";s:12:"instructions";s:0:"";s:8:"required";s:1:"0";s:13:"default_value";s:0:"";s:7:"toolbar";s:4:"full";s:12:"media_upload";s:3:"yes";s:17:"conditional_logic";a:3:{s:6:"status";s:1:"0";s:5:"rules";a:1:{i:0;a:3:{s:5:"field";s:4:"null";s:8:"operator";s:2:"==";s:5:"value";s:0:"";}}s:8:"allorany";s:3:"all";}s:8:"order_no";i:1;}'),
(417, 56, 'producers_Title', 'Fully Insured versus Self-Funded'),
(418, 56, '_producers_Title', 'field_54d8f8cc3ae56'),
(419, 56, 'producers_content', '<div class="second-title border-bottom">\r\n<h2><span class="line"> <span class="min">for</span> Your Company <span class="min">?</span> </span></h2>\r\n</div>\r\n<div class="col-xs-24 no-pad">\r\n<h3 class="subtitle-blue">Fully Insured versus Self-Funded</h3>\r\n<div class="col-xs-24 content-info">\r\n<p class="text">Cook Group Solutions is a leading provider of third party administrative (TPA) services and medical stop-loss insurance services and products to the private-sector employer market. We specialize in working with employers transitioning from fully insured health benefits to self-insurance. More than protection, we pride ourselves in offering the flexibility, knowledge and resources our partners need to solve today’s unique healthcare challenges.</p>\r\n<p class="text">At Cook Group Solutions, we work with private sector employers, plan sponsors, independent agents and brokers, benefits consultants and self-insurance service providers to provide fair, competitive premiums backed by A-rated issuing insurance carriers and re-insurers.</p>\r\n\r\n</div>\r\n</div>'),
(420, 56, '_producers_content', 'field_54d8f918e82e4'),
(421, 53, 'producers_Title', 'teste'),
(422, 53, '_producers_Title', 'field_54d8fd83fe2c0'),
(423, 53, 'producers_content', 'At Cook Group Solutions, we work with private sector employers, plan sponsors, independent agents and brokers, benefits consultants and self-insurance service providers to provide fair, competitive premiums backed by A-rated issuing insurance carriers and re-insurers. We specialize in working with employers transitioning from fully insured health benefits to self-insurance. More than protection, we pride ourselves in offering the flexibility, knowledge and resources our partners need to solve today’s unique healthcare challenges.\r\n<ul>\r\n	<li>\r\n<h3>Specialists in transitioning employers from fully insured to self-insured health benefit plans</h3>\r\n</li>\r\n	<li>\r\n<h3>Health statements may not be required (case dependent)</h3>\r\n</li>\r\n	<li>\r\n<h3>"A"-rated issuing carriers</h3>\r\n</li>\r\n	<li>\r\n<h3>Competitive commissions</h3>\r\n</li>\r\n	<li>\r\n<h3>Multiple underwriting markets</h3>\r\n</li>\r\n</ul>'),
