(4949, 648, 'home_main_content', '<b>Since 1974, Cook Group has been a leading provider</b> of third party administration (TPA) services and medical stop-loss insurance products to the private-sector employer health benefits marketplace.'),
(4944, 648, '_home_Home Main Contentmain_content', 'field_54d4de99acaa5'),
(4759, 647, 'home_Home Main Contentmain_content', '<b>Since 1974 Cook Group has been a leading provider</b> of third party administrative (TPA) services and medical stop-loss insurance products to the private-sector employer.'),
(4760, 647, '_home_Home Main Contentmain_content', 'field_54d4de99acaa5'),
(4761, 647, 'home_list_title', 'Protection. Flexibility. Access. <span></span>'),
(4762, 647, '_home_list_title', 'field_54d4dffe77262'),
(4763, 647, 'Home Main Content', '<span style="color: #266473;"><strong>Kathleen Gettelfinger, LMFT</strong></span>, As a Licensed Marriage and Family Therapist, I know firsthand the power of a safe and secure space that allows for exploration and examination. Seeking therapy is an act of both courage and vulnerability, and I meet these strengths with compassion, warmth, and understanding. Together, I work with clients to co-create an experience that facilitates honest reflection and a movement toward the change they seek. It is my privilege to accompany and support clients on this powerful journey of self-discovery.'),
(4764, 647, '_Home Main Content', 'field_54d4de99acaa5'),
(4765, 647, 'home_main_content', '<b>Since 1974, Cook Group has been a leading provider</b> of third party administration (TPA) services and medical stop-loss insurance products to the private-sector employer health benefits marketplace.'),
(4766, 647, '_home_main_content', 'field_54d4de99acaa5'),
